import{_ as a,c as e,o as s,a as n}from"./app.06b4d43c.js";const g=JSON.parse('{"title":"Configure system","description":"","frontmatter":{},"headers":[{"level":2,"title":"The nano text editor","slug":"the-nano-text-editor"},{"level":2,"title":"Setup hostname","slug":"setup-hostname"},{"level":2,"title":"Setup locale","slug":"setup-locale"},{"level":2,"title":"Setup time & date","slug":"setup-time-date"},{"level":2,"title":"Configure pacman","slug":"configure-pacman"},{"level":3,"title":"Setup multilib","slug":"setup-multilib"},{"level":3,"title":"Enable parallel downloads","slug":"enable-parallel-downloads"},{"level":3,"title":"Extra candy","slug":"extra-candy"},{"level":3,"title":"After configuring","slug":"after-configuring"}],"relativePath":"installation/configure-system.md","lastUpdated":1660733218000}'),l={name:"installation/configure-system.md"},o=n(`<h1 id="configure-system" tabindex="-1">Configure system <a class="header-anchor" href="#configure-system" aria-hidden="true">#</a></h1><h2 id="the-nano-text-editor" tabindex="-1">The nano text editor <a class="header-anchor" href="#the-nano-text-editor" aria-hidden="true">#</a></h2><p>Nano is the text editor we will use in this tutorial. Basic Usage:</p><ul><li>Move with arrow keys</li><li><code>CTRL + O</code> and then <code>ENTER</code> to save</li><li><code>CTRL + X</code> to exit</li></ul><h2 id="setup-hostname" tabindex="-1">Setup hostname <a class="header-anchor" href="#setup-hostname" aria-hidden="true">#</a></h2><p>\u{1F4DB} This will be the name of your PC on your network (Replace <code>myhostname</code>)</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> myhostname </span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> /etc/hostname</span></span>
<span class="line"><span style="color:#A6ACCD;">nano /etc/hosts</span></span>
<span class="line"></span></code></pre></div><p>Add these lines</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">127.0.0.1   localhost</span></span>
<span class="line"><span style="color:#A6ACCD;">::1         localhost</span></span>
<span class="line"><span style="color:#A6ACCD;">127.0.1.1   myhostname.localdomain  myhostname</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><h2 id="setup-locale" tabindex="-1">Setup locale <a class="header-anchor" href="#setup-locale" aria-hidden="true">#</a></h2><p>\u{1F310} Uncomment (remove the # in front of) all languages you need</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">nano /etc/locale.gen</span></span>
<span class="line"></span></code></pre></div><p>\u{1F3C1} Generate locales</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">locale-gen</span></span>
<span class="line"></span></code></pre></div><p>\u{1F518} Set locale</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> LANG=en_US.UTF-8 </span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> /etc/locale.conf</span></span>
<span class="line"><span style="color:#C792EA;">export</span><span style="color:#A6ACCD;"> LANG=en_US.UTF-8</span></span>
<span class="line"></span></code></pre></div><p>\u2328\uFE0F Set tty keymap (replace <code>yourkeymap</code> with your keymap e.g. <code>de-latin1</code>)</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> KEYMAP=yourkeymap </span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> /etc/vconsole.conf</span></span>
<span class="line"></span></code></pre></div><h2 id="setup-time-date" tabindex="-1">Setup time &amp; date <a class="header-anchor" href="#setup-time-date" aria-hidden="true">#</a></h2><p>\u{1F4C5} You can tab-complete your stuff after zoneinfo</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">ln -sf /usr/share/zoneinfo/Region/City /etc/localtime</span></span>
<span class="line"><span style="color:#A6ACCD;">hwclock --systohc --utc</span></span>
<span class="line"></span></code></pre></div><h2 id="configure-pacman" tabindex="-1">Configure pacman <a class="header-anchor" href="#configure-pacman" aria-hidden="true">#</a></h2><p>Edit pacman configuration file</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">nano /etc/pacman.conf</span></span>
<span class="line"></span></code></pre></div><h3 id="setup-multilib" tabindex="-1">Setup multilib <a class="header-anchor" href="#setup-multilib" aria-hidden="true">#</a></h3><p>\u{1F47E} multilib is a repository which contains 32-bit libraries and is disabled by default (needed for some games &amp; software; highly recommended to enable)</p><p>\u{1F4A5} Uncomment (remove the # in front of) the following lines</p><div class="language-ini"><button class="copy"></button><span class="lang">ini</span><pre><code><span class="line"><span style="color:#89DDFF;">[multilib]</span></span>
<span class="line"><span style="color:#F07178;">Include</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> /etc/pacman.d/mirrorlist</span></span>
<span class="line"></span></code></pre></div><h3 id="enable-parallel-downloads" tabindex="-1">Enable parallel downloads <a class="header-anchor" href="#enable-parallel-downloads" aria-hidden="true">#</a></h3><p>\u{1F310} Depending on your internet connection enabling parallel downloads may speed up the package download process</p><p>\u{1F4A5} Uncomment (remove the # in front of) this line and set it to your desired value</p><div class="language-ini"><button class="copy"></button><span class="lang">ini</span><pre><code><span class="line"><span style="color:#F07178;">ParallelDownloads</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> 5</span></span>
<span class="line"></span></code></pre></div><h3 id="extra-candy" tabindex="-1">Extra candy <a class="header-anchor" href="#extra-candy" aria-hidden="true">#</a></h3><p>\u{1F36C} If you want some extra candy you can uncomment <code>Color</code> and <code>VerbosePkgLists</code> and add <code>ILoveCandy</code> under <code>Misc options</code>.</p><h3 id="after-configuring" tabindex="-1">After configuring <a class="header-anchor" href="#after-configuring" aria-hidden="true">#</a></h3><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">pacman -Syu</span></span>
<span class="line"></span></code></pre></div>`,36),t=[o];function p(c,i,r,d,u,h){return s(),e("div",null,t)}const m=a(l,[["render",p]]);export{g as __pageData,m as default};
