import{_ as e,c as a,o as s,a as n}from"./app.7dec25d4.js";const m=JSON.parse('{"title":"Install Desktop","description":"","frontmatter":{},"headers":[{"level":2,"title":"Display Server","slug":"display-server","link":"#display-server","children":[]},{"level":2,"title":"Desktop Environment","slug":"desktop-environment","link":"#desktop-environment","children":[{"level":3,"title":"KDE Plasma","slug":"kde-plasma","link":"#kde-plasma","children":[]},{"level":3,"title":"Xfce","slug":"xfce","link":"#xfce","children":[]},{"level":3,"title":"GNOME","slug":"gnome","link":"#gnome","children":[]},{"level":3,"title":"LXDE","slug":"lxde","link":"#lxde","children":[]},{"level":3,"title":"LXQt","slug":"lxqt","link":"#lxqt","children":[]},{"level":3,"title":"Cinnamon","slug":"cinnamon","link":"#cinnamon","children":[]},{"level":3,"title":"Budgie","slug":"budgie","link":"#budgie","children":[]},{"level":3,"title":"Mate","slug":"mate","link":"#mate","children":[]},{"level":3,"title":"Deepin","slug":"deepin","link":"#deepin","children":[]}]},{"level":2,"title":"Display/Desktop Manager","slug":"display-desktop-manager","link":"#display-desktop-manager","children":[{"level":3,"title":"LXDM (Included with LXDE)","slug":"lxdm-included-with-lxde","link":"#lxdm-included-with-lxde","children":[]},{"level":3,"title":"SDDM (Included with KDE Plasma)","slug":"sddm-included-with-kde-plasma","link":"#sddm-included-with-kde-plasma","children":[]},{"level":3,"title":"GDM (Included with GNOME/Budgie/MATE)","slug":"gdm-included-with-gnome-budgie-mate","link":"#gdm-included-with-gnome-budgie-mate","children":[]},{"level":3,"title":"LightDM","slug":"lightdm","link":"#lightdm","children":[]}]}],"relativePath":"installation/install-desktop.md","lastUpdated":1668518575000}'),l={name:"installation/install-desktop.md"},i=n(`<h1 id="install-desktop" tabindex="-1">Install Desktop <a class="header-anchor" href="#install-desktop" aria-hidden="true">#</a></h1><h2 id="display-server" tabindex="-1">Display Server <a class="header-anchor" href="#display-server" aria-hidden="true">#</a></h2><p>\u{1F5A5}\uFE0F Xorg is the display server we will use</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S xorg-server xorg-xinit xorg-xrandr xorg-xfontsel xorg-xlsfonts xorg-xkill xorg-xinput xorg-xwininfo</span></span>
<span class="line"></span></code></pre></div><h2 id="desktop-environment" tabindex="-1">Desktop Environment <a class="header-anchor" href="#desktop-environment" aria-hidden="true">#</a></h2><p>\u{1F5D4} You need to select a desktop environment</p><ul><li>For beginners coming from Windows I recommend KDE Plasma or Cinnmaon.</li><li>For a very resource friendy desktop I recommend Xfce or LXQt</li><li><strong>The instructions for KDE Plasma are tested by me because I use it. Others should work but you may need some extra packages for productive use (pull requests are welcome)</strong></li></ul><h3 id="kde-plasma" tabindex="-1">KDE Plasma <a class="header-anchor" href="#kde-plasma" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S plasma kdialog packagekit-qt5 kcalc icoutils libappimage konsole dolphin kdegraphics-thumbnailers svgpart ffmpegthumbs kdenetwork-filesharing gwenview kimageformats ark kate okular kcron kdf filelight print-manager</span></span>
<span class="line"></span></code></pre></div><p>If you want to use KDE Connect (Pairing with Android phone)</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S kdeconnect sshfs</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/KDE" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/KDE</a></p><h3 id="xfce" tabindex="-1">Xfce <a class="header-anchor" href="#xfce" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S xfce4 xfce4-goodies</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/Xfce" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/Xfce</a></p><h3 id="gnome" tabindex="-1">GNOME <a class="header-anchor" href="#gnome" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S gnome gnome-extra</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/GNOME" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/GNOME</a></p><h3 id="lxde" tabindex="-1">LXDE <a class="header-anchor" href="#lxde" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S lxde lxdm-gtk3</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/LXDE" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/LXDE</a></p><h3 id="lxqt" tabindex="-1">LXQt <a class="header-anchor" href="#lxqt" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S lxqt breeze-icons pcmanfm-qt qterminal lxqt-sudo polkit-qt5</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/LXQt" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/LXQt</a></p><h3 id="cinnamon" tabindex="-1">Cinnamon <a class="header-anchor" href="#cinnamon" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S cinnamon cinnamon-translations nemo-fileroller nemo-image-converter nemo-preview xed xreader gnome-terminal metacity gnome-shell</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/Cinnamon" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/Cinnamon</a></p><h3 id="budgie" tabindex="-1">Budgie <a class="header-anchor" href="#budgie" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S budgie-desktop network-manager-applet gnome</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/Budgie" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/Budgie</a></p><h3 id="mate" tabindex="-1">Mate <a class="header-anchor" href="#mate" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S mate mate-extra gdm</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/MATE" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/MATE</a></p><h3 id="deepin" tabindex="-1">Deepin <a class="header-anchor" href="#deepin" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S deepin deepin-extra</span></span>
<span class="line"><span style="color:#A6ACCD;">nano /etc/lightdm/lightdm.conf</span></span>
<span class="line"><span style="color:#676E95;"># append this line to use the deepin specific greeter</span></span>
<span class="line"><span style="color:#A6ACCD;">greeter-session=lightdm-deepin-greeter</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/Deepin" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/Deepin</a></p><h2 id="display-desktop-manager" tabindex="-1">Display/Desktop Manager <a class="header-anchor" href="#display-desktop-manager" aria-hidden="true">#</a></h2><p>\u{1F5A5}\uFE0F A display manager is basically your login screen where you enter your user details and select your Desktop Environment</p><h3 id="lxdm-included-with-lxde" tabindex="-1">LXDM (Included with LXDE) <a class="header-anchor" href="#lxdm-included-with-lxde" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S lxdm-gtk3</span></span>
<span class="line"><span style="color:#A6ACCD;">systemctl </span><span style="color:#82AAFF;">enable</span><span style="color:#A6ACCD;"> lxdm</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/LXDM" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/LXDM</a></p><h3 id="sddm-included-with-kde-plasma" tabindex="-1">SDDM (Included with KDE Plasma) <a class="header-anchor" href="#sddm-included-with-kde-plasma" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S sddm</span></span>
<span class="line"><span style="color:#A6ACCD;">systemctl </span><span style="color:#82AAFF;">enable</span><span style="color:#A6ACCD;"> sddm</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/SDDM" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/SDDM</a></p><h3 id="gdm-included-with-gnome-budgie-mate" tabindex="-1">GDM (Included with GNOME/Budgie/MATE) <a class="header-anchor" href="#gdm-included-with-gnome-budgie-mate" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S gdm</span></span>
<span class="line"><span style="color:#A6ACCD;">systemctl </span><span style="color:#82AAFF;">enable</span><span style="color:#A6ACCD;"> gdm</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/GDM" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/GDM</a></p><h3 id="lightdm" tabindex="-1">LightDM <a class="header-anchor" href="#lightdm" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S lightdm lightdm-gtk-greeter</span></span>
<span class="line"><span style="color:#A6ACCD;">systemctl </span><span style="color:#82AAFF;">enable</span><span style="color:#A6ACCD;"> lightdm</span></span>
<span class="line"></span></code></pre></div><p>See also <a href="https://wiki.archlinux.org/index.php/LightDM" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/LightDM</a></p>`,50),t=[i];function p(r,o,d,c,h,g){return s(),a("div",null,t)}const k=e(l,[["render",p]]);export{m as __pageData,k as default};
