import{_ as a,c as s,o as e,a as n}from"./app.e924681e.js";const v=JSON.parse('{"title":"Useful packages","description":"","frontmatter":{},"headers":[{"level":2,"title":"General packages","slug":"general-packages","link":"#general-packages","children":[]},{"level":2,"title":"Printer support","slug":"printer-support","link":"#printer-support","children":[{"level":3,"title":"General packages","slug":"general-packages-1","link":"#general-packages-1","children":[]},{"level":3,"title":"Qt Scan Application","slug":"qt-scan-application","link":"#qt-scan-application","children":[]},{"level":3,"title":"GTK Scan Application","slug":"gtk-scan-application","link":"#gtk-scan-application","children":[]},{"level":3,"title":"UI for HP Printers","slug":"ui-for-hp-printers","link":"#ui-for-hp-printers","children":[]}]},{"level":2,"title":"Input Driver","slug":"input-driver","link":"#input-driver","children":[]},{"level":2,"title":"Graphics Driver","slug":"graphics-driver","link":"#graphics-driver","children":[{"level":3,"title":"Mesa","slug":"mesa","link":"#mesa","children":[]},{"level":3,"title":"Vulkan","slug":"vulkan","link":"#vulkan","children":[]},{"level":3,"title":"Open Source drivers","slug":"open-source-drivers","link":"#open-source-drivers","children":[]},{"level":3,"title":"Nvidia proprietary driver","slug":"nvidia-proprietary-driver","link":"#nvidia-proprietary-driver","children":[]},{"level":3,"title":"AMD Utils","slug":"amd-utils","link":"#amd-utils","children":[]},{"level":3,"title":"Intel Utils","slug":"intel-utils","link":"#intel-utils","children":[]},{"level":3,"title":"Add kernel module","slug":"add-kernel-module","link":"#add-kernel-module","children":[]}]},{"level":2,"title":"Networking","slug":"networking","link":"#networking","children":[{"level":3,"title":"Wi-Fi connection","slug":"wi-fi-connection","link":"#wi-fi-connection","children":[]}]},{"level":2,"title":"Some archive and file system utils","slug":"some-archive-and-file-system-utils","link":"#some-archive-and-file-system-utils","children":[]},{"level":2,"title":"Sound","slug":"sound","link":"#sound","children":[{"level":3,"title":"PulseAudio","slug":"pulseaudio","link":"#pulseaudio","children":[]},{"level":3,"title":"PipeWire","slug":"pipewire","link":"#pipewire","children":[]},{"level":3,"title":"Control app","slug":"control-app","link":"#control-app","children":[]}]},{"level":2,"title":"Bluetooth support","slug":"bluetooth-support","link":"#bluetooth-support","children":[]},{"level":2,"title":"ADB","slug":"adb","link":"#adb","children":[]},{"level":2,"title":"Other shells","slug":"other-shells","link":"#other-shells","children":[{"level":3,"title":"zsh (Z Shell)","slug":"zsh-z-shell","link":"#zsh-z-shell","children":[]},{"level":3,"title":"fish (Friendly interactive shell)","slug":"fish-friendly-interactive-shell","link":"#fish-friendly-interactive-shell","children":[]}]}],"relativePath":"installation/useful-packages.md","lastUpdated":1668086158000}'),l={name:"installation/useful-packages.md"},i=n(`<h1 id="useful-packages" tabindex="-1">Useful packages <a class="header-anchor" href="#useful-packages" aria-hidden="true">#</a></h1><h2 id="general-packages" tabindex="-1">General packages <a class="header-anchor" href="#general-packages" aria-hidden="true">#</a></h2><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S linux-headers dkms</span></span>
<span class="line"><span style="color:#A6ACCD;">pacman -S jshon expac git wget acpid avahi net-tools xdg-user-dirs</span></span>
<span class="line"><span style="color:#A6ACCD;">systemctl </span><span style="color:#82AAFF;">enable</span><span style="color:#A6ACCD;"> acpid avahi-daemon systemd-timesyncd</span></span>
<span class="line"></span></code></pre></div><p>If system is running on an SSD</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">systemctl </span><span style="color:#82AAFF;">enable</span><span style="color:#A6ACCD;"> fstrim.timer</span></span>
<span class="line"></span></code></pre></div><h2 id="printer-support" tabindex="-1">Printer support <a class="header-anchor" href="#printer-support" aria-hidden="true">#</a></h2><p>\u{1F5A8}\uFE0F Add some packages needed for printing and scanning</p><h3 id="general-packages-1" tabindex="-1">General packages <a class="header-anchor" href="#general-packages-1" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S system-config-printer foomatic-db foomatic-db-engine gutenprint gsfonts cups cups-pdf cups-filters sane</span></span>
<span class="line"><span style="color:#A6ACCD;">systemctl </span><span style="color:#82AAFF;">enable</span><span style="color:#A6ACCD;"> cups.service saned.socket</span></span>
<span class="line"></span></code></pre></div><h3 id="qt-scan-application" tabindex="-1">Qt Scan Application <a class="header-anchor" href="#qt-scan-application" aria-hidden="true">#</a></h3><p>Use this if you use KDE Plasma or LXQt</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S skanlite</span></span>
<span class="line"></span></code></pre></div><h3 id="gtk-scan-application" tabindex="-1">GTK Scan Application <a class="header-anchor" href="#gtk-scan-application" aria-hidden="true">#</a></h3><p>Use this if you use another desktop environment</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S simple-scan</span></span>
<span class="line"></span></code></pre></div><h3 id="ui-for-hp-printers" tabindex="-1">UI for HP Printers <a class="header-anchor" href="#ui-for-hp-printers" aria-hidden="true">#</a></h3><p>\u{1F5A8} Install this if you have an HP Printer</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S hplip</span></span>
<span class="line"></span></code></pre></div><h2 id="input-driver" tabindex="-1">Input Driver <a class="header-anchor" href="#input-driver" aria-hidden="true">#</a></h2><p>These are some packages needed for certain input devices to function properly. It does no harm to install them, even if you wouldn&#39;t need them</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S xf86-input-synaptics xf86-input-libinput xf86-input-evdev</span></span>
<span class="line"></span></code></pre></div><p>When installing inside a virtual machine:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S xf86-input-vmmouse</span></span>
<span class="line"></span></code></pre></div><h2 id="graphics-driver" tabindex="-1">Graphics Driver <a class="header-anchor" href="#graphics-driver" aria-hidden="true">#</a></h2><h3 id="mesa" tabindex="-1">Mesa <a class="header-anchor" href="#mesa" aria-hidden="true">#</a></h3><p>This is useful for all GPUs</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S mesa lib32-mesa</span></span>
<span class="line"></span></code></pre></div><h3 id="vulkan" tabindex="-1">Vulkan <a class="header-anchor" href="#vulkan" aria-hidden="true">#</a></h3><p>This is useful for all GPUs</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S vulkan-icd-loader lib32-vulkan-icd-loader</span></span>
<span class="line"></span></code></pre></div><h3 id="open-source-drivers" tabindex="-1">Open Source drivers <a class="header-anchor" href="#open-source-drivers" aria-hidden="true">#</a></h3><p>Only install this if you use an AMD or Intel GPU or want to use the open source NVIDIA driver (Nouveau, not developed by NVIDIA)</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S </span><span style="color:#89DDFF;">&lt;</span><span style="color:#A6ACCD;">driver</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span></code></pre></div><ul><li><code>xf86-video-amdgpu</code> is for newer AMD GPUs</li><li><code>xf86-video-nouveau</code> is the open source NVIDIA driver</li><li><code>xf86-video-intel</code> is the open source Intel driver (You probably want to leave this out, and it will use the modesetting driver. For more information refer to <a href="https://wiki.archlinux.org/index.php/Intel_graphics#Installation" target="_blank" rel="noreferrer">the wiki</a>)</li><li><code>xf86-video-ati</code> is for older AMD GPUs</li><li><code>xf86-video-vmware</code> for VirtualBox, VMWare, QEMU</li><li><code>xf86-video-fbdev</code> for Hyper-V</li><li>If you don&#39;t know it you can install all, but it could happen that the internal graphics card is used if you install the driver for it</li></ul><h3 id="nvidia-proprietary-driver" tabindex="-1">Nvidia proprietary driver <a class="header-anchor" href="#nvidia-proprietary-driver" aria-hidden="true">#</a></h3><p>Only install these packages if you are using an NVIDIA GPU</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S nvidia nvidia-utils lib32-nvidia-utils libvdpau lib32-libvdpau</span></span>
<span class="line"></span></code></pre></div><h3 id="amd-utils" tabindex="-1">AMD Utils <a class="header-anchor" href="#amd-utils" aria-hidden="true">#</a></h3><p>Only install these packages if you are using an AMD GPU</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S libva-mesa-driver lib32-libva-mesa-driver mesa-vdpau lib32-mesa-vdpau libva-vdpau-driver lib32-libva-vdpau-driver vulkan-radeon lib32-vulkan-radeon</span></span>
<span class="line"></span></code></pre></div><h3 id="intel-utils" tabindex="-1">Intel Utils <a class="header-anchor" href="#intel-utils" aria-hidden="true">#</a></h3><p>Only install this package if you are using an Intel GPU</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S vulkan-intel</span></span>
<span class="line"></span></code></pre></div><h3 id="add-kernel-module" tabindex="-1">Add kernel module <a class="header-anchor" href="#add-kernel-module" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">nano /etc/mkinitcpio.conf</span></span>
<span class="line"></span></code></pre></div><p>Change <code>MODULES=()</code> to</p><ul><li><code>MODULES=(amdgpu)</code> if you installed <code>xf86-video-amdgpu</code></li><li><code>MODULES=(nvidia)</code> if you installed <code>nvidia</code></li><li><code>MODULES=(i915)</code> if you installed <code>xf86-video-intel</code></li><li>for any other driver you can skip this step</li></ul><p>and run</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">mkinitcpio -P</span></span>
<span class="line"></span></code></pre></div><h2 id="networking" tabindex="-1">Networking <a class="header-anchor" href="#networking" aria-hidden="true">#</a></h2><p>\u{1F5A7} Those are essential networking tools</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S networkmanager networkmanager-openvpn networkmanager-pptp networkmanager-vpnc</span></span>
<span class="line"><span style="color:#A6ACCD;">systemctl </span><span style="color:#82AAFF;">enable</span><span style="color:#A6ACCD;"> NetworkManager</span></span>
<span class="line"></span></code></pre></div><h3 id="wi-fi-connection" tabindex="-1">Wi-Fi connection <a class="header-anchor" href="#wi-fi-connection" aria-hidden="true">#</a></h3><p>\u{1F4F6} Those are essential tools if you connect to the internet via Wi-Fi</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S wireless_tools wpa_supplicant ifplugd dialog</span></span>
<span class="line"></span></code></pre></div><h2 id="some-archive-and-file-system-utils" tabindex="-1">Some archive and file system utils <a class="header-anchor" href="#some-archive-and-file-system-utils" aria-hidden="true">#</a></h2><p>\u{1F5C4}\uFE0F Important tools for archives and file systems</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S p7zip unrar unarchiver unzip unace xz rsync</span></span>
<span class="line"><span style="color:#A6ACCD;">pacman -S nfs-utils cifs-utils ntfs-3g exfat-utils</span></span>
<span class="line"></span></code></pre></div><h2 id="sound" tabindex="-1">Sound <a class="header-anchor" href="#sound" aria-hidden="true">#</a></h2><p>\u{1F50A} Some essential packages for sound</p><h3 id="pulseaudio" tabindex="-1">PulseAudio <a class="header-anchor" href="#pulseaudio" aria-hidden="true">#</a></h3><p>Default audio server for Linux</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S alsa-utils pulseaudio-alsa pulseaudio-equalizer</span></span>
<span class="line"></span></code></pre></div><p>See <a href="https://wiki.archlinux.org/title/PulseAudio" target="_blank" rel="noreferrer">https://wiki.archlinux.org/title/PulseAudio</a></p><h4 id="fix-muting-some-media-players" tabindex="-1">Fix muting some media players <a class="header-anchor" href="#fix-muting-some-media-players" aria-hidden="true">#</a></h4><p>\u{1F507} PulseAudio by default might mute media applications when entering a voice call. To disable this:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">mkdir -p /etc/pulse/default.pa.d</span></span>
<span class="line"><span style="color:#A6ACCD;">nano /etc/pulse/default.pa.d/no-cork.pa</span></span>
<span class="line"></span></code></pre></div><p>Put this in the file and save it:</p><div class="language-ini"><button title="Copy Code" class="copy"></button><span class="lang">ini</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">unload-module module-role-cork</span></span>
<span class="line"></span></code></pre></div><p>Source: <a href="https://wiki.archlinux.org/index.php/PulseAudio#Disable_muting_media_on_entering_voice_call_(module-role-cork)" target="_blank" rel="noreferrer">https://wiki.archlinux.org/index.php/PulseAudio#Disable_muting_media_on_entering_voice_call_(module-role-cork)</a></p><h3 id="pipewire" tabindex="-1">PipeWire <a class="header-anchor" href="#pipewire" aria-hidden="true">#</a></h3><p>Alternatively use can PipeWire:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S alsa-utils pipewire pipewire-pulse pipewire-jack wireplumber</span></span>
<span class="line"></span></code></pre></div><p>See <a href="https://wiki.archlinux.org/title/PipeWire" target="_blank" rel="noreferrer">https://wiki.archlinux.org/title/PipeWire</a></p><h3 id="control-app" tabindex="-1">Control app <a class="header-anchor" href="#control-app" aria-hidden="true">#</a></h3><p>For Qt based desktops (e.g. KDE Plasma or LXQt)</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S pavucontrol-qt</span></span>
<span class="line"></span></code></pre></div><p>For GTK based desktop (another desktop environment)</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S pavucontrol</span></span>
<span class="line"></span></code></pre></div><h2 id="bluetooth-support" tabindex="-1">Bluetooth support <a class="header-anchor" href="#bluetooth-support" aria-hidden="true">#</a></h2><p>\u{1F535} Add some packages needed for proper bluetooth support</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S bluez bluez-utils</span></span>
<span class="line"><span style="color:#A6ACCD;">systemctl </span><span style="color:#82AAFF;">enable</span><span style="color:#A6ACCD;"> bluetooth</span></span>
<span class="line"><span style="color:#676E95;"># only if you installed PulseAudio in the previous step:</span></span>
<span class="line"><span style="color:#A6ACCD;">pacman -S pulseaudio-bluetooth</span></span>
<span class="line"></span></code></pre></div><h2 id="adb" tabindex="-1">ADB <a class="header-anchor" href="#adb" aria-hidden="true">#</a></h2><p>If you want the Android Debug Bridge working with your user:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S android-tools android-udev</span></span>
<span class="line"><span style="color:#A6ACCD;">usermod -aG adbusers yourusername</span></span>
<span class="line"></span></code></pre></div><h2 id="other-shells" tabindex="-1">Other shells <a class="header-anchor" href="#other-shells" aria-hidden="true">#</a></h2><p>\u{1F41A} You may want to use another shell than bash</p><h3 id="zsh-z-shell" tabindex="-1">zsh (Z Shell) <a class="header-anchor" href="#zsh-z-shell" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S zsh zsh-completions</span></span>
<span class="line"><span style="color:#A6ACCD;">chsh -s /usr/bin/zsh yourusername</span></span>
<span class="line"></span></code></pre></div><h3 id="fish-friendly-interactive-shell" tabindex="-1">fish (Friendly interactive shell) <a class="header-anchor" href="#fish-friendly-interactive-shell" aria-hidden="true">#</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki"><code><span class="line"><span style="color:#A6ACCD;">pacman -S fish</span></span>
<span class="line"><span style="color:#A6ACCD;">chsh -s /usr/bin/fish yourusername</span></span>
<span class="line"></span></code></pre></div>`,91),p=[i];function t(o,r,c,d,h,u){return e(),s("div",null,p)}const b=a(l,[["render",t]]);export{v as __pageData,b as default};
